import React, { Component, PropTypes } from 'react';
import { DragSource } from 'react-dnd';
import ItemTypes from 'ItemTypes';
import Wad from 'web-audio-daw'

const style = {
  border: '3px solid black',
  borderRadius: '10px',
  backgroundColor: '#FFD503',
  padding: '0.2rem 0.5rem',
  marginRight: '0.5rem',
  marginBottom: '0.4rem',
  cursor: 'move',
  float: 'left',
  width: '8rem',
  textAlign: 'center',
  fontSize: '12px'
};
const flute = new Wad({
    source : 'square',
    env : {
        attack : .3,
        decay : .002,
        sustain : .2,
        hold : 10,
        release : .3
    },
    filter : {
        type : 'lowpass',
        frequency : 400,
        q : 7,
        env : {
            attack : .7,
            frequency : 1600
        }
    },
    vibrato : {
        attack : 8,
        speed : 8,
        magnitude : 100
    }
})

const studentSource = {
  beginDrag(props) {
    flute.play({ panning :  .1, pitch : 'D4' ,loop: true })
    return {
      name: props.name,
      id: props.id
    };

  },

  endDrag(props, monitor) {
    const item = monitor.getItem();
    const dropResult = monitor.getDropResult();
    flute.stop()

    //if (dropResult) {
    //  window.alert( // eslint-disable-line no-alert
    //    `${item.name} have chosen ${dropResult.name} for Learning Agreement!`,
    //  );
    //}
  },
};

@DragSource(ItemTypes.STUDENT, studentSource, (connect, monitor) => ({
  connectDragSource: connect.dragSource(),
  connectDragPreview: connect.dragPreview(),
  isDragging: monitor.isDragging(),
}))
export default class Student extends Component {
  static propTypes = {
    connectDragSource: PropTypes.func.isRequired,
    connectDragPreview: PropTypes.func.isRequired,
    isDragging: PropTypes.bool.isRequired,
    name: PropTypes.string.isRequired,
    id: PropTypes.string.isRequired
  };

  componentDidMount() {
    const img = new Image();
    img.src = 'data:image/false;base64,'
    img.onload = () => this.props.connectDragPreview(img);
  }

  render() {
    const { isDragging, connectDragSource } = this.props;
    const { name } = this.props;
    const opacity = isDragging ? 0.4 : 1;

    return (
      connectDragSource(
        <div style={{ ...style, opacity }}>
          <b style={{fontSize: '140%'}}>{name.substring(0,1)}</b>{name.substring(1,name.length)}
        </div>,
      )
    );
  }
}
